"use client";

import * as React from "react";
import {
  Home,
  Users,
  Settings,
  FileText,
  Calendar,
  BarChart3,
  ChevronLeft,
  ChevronRight,
  LucideIcon,
} from "lucide-react";

import { cn } from "@/lib/utils";
import { Button } from "@/registry/vitality/ui/button";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/registry/vitality/ui/tooltip";

interface SideNavItem {
  label: string;
  icon: LucideIcon;
  href?: string;
  onClick?: () => void;
  isActive?: boolean;
}

interface SideNavProps extends React.HTMLAttributes<HTMLElement> {
  items?: SideNavItem[];
  defaultExpanded?: boolean;
  header?: React.ReactNode;
  footer?: React.ReactNode;
  onExpandedChange?: (expanded: boolean) => void;
}

const defaultNavItems: SideNavItem[] = [
  { label: "Home", icon: Home, href: "/" },
  { label: "Patients", icon: Users, href: "/patients" },
  { label: "Appointments", icon: Calendar, href: "/appointments" },
  { label: "Documents", icon: FileText, href: "/documents" },
  { label: "Reports", icon: BarChart3, href: "/reports" },
  { label: "Settings", icon: Settings, href: "/settings" },
];

function SideNavItemComponent({
  item,
  isExpanded,
}: {
  item: SideNavItem;
  isExpanded: boolean;
}) {
  const Icon = item.icon;

  const content = (
    <Button
      variant={item.isActive ? "secondary" : "ghost"}
      className={cn(
        "w-full justify-start gap-3 h-10",
        !isExpanded && "justify-center px-0"
      )}
      asChild={!!item.href}
      onClick={item.onClick}
    >
      {item.href ? (
        <a href={item.href}>
          <Icon className="size-5 shrink-0" />
          {isExpanded && <span className="truncate">{item.label}</span>}
        </a>
      ) : (
        <>
          <Icon className="size-5 shrink-0" />
          {isExpanded && <span className="truncate">{item.label}</span>}
        </>
      )}
    </Button>
  );

  if (!isExpanded) {
    return (
      <Tooltip delayDuration={0}>
        <TooltipTrigger asChild>{content}</TooltipTrigger>
        <TooltipContent side="right" sideOffset={8}>
          {item.label}
        </TooltipContent>
      </Tooltip>
    );
  }

  return content;
}

function SideNav({
  className,
  items = defaultNavItems,
  defaultExpanded = true,
  header,
  footer,
  onExpandedChange,
  ...props
}: SideNavProps) {
  const [isExpanded, setIsExpanded] = React.useState(defaultExpanded);

  const toggleExpanded = () => {
    const newExpanded = !isExpanded;
    setIsExpanded(newExpanded);
    onExpandedChange?.(newExpanded);
  };

  return (
    <TooltipProvider>
      <aside
        className={cn(
          "relative flex flex-col h-screen border-r bg-background transition-all duration-300 ease-in-out",
          isExpanded ? "w-[300px]" : "w-[56px]",
          className
        )}
        {...props}
      >
        {/* Header */}
        {header && (
          <div
            className={cn(
              "flex items-center border-b h-12 shrink-0",
              isExpanded ? "px-4" : "justify-center px-2"
            )}
          >
            {header}
          </div>
        )}

        {/* Navigation items */}
        <nav
          className={cn(
            "flex-1 overflow-y-auto py-2",
            isExpanded ? "px-3" : "px-2"
          )}
        >
          <ul className="space-y-1">
            {items.map((item, index) => (
              <li key={index}>
                <SideNavItemComponent item={item} isExpanded={isExpanded} />
              </li>
            ))}
          </ul>
        </nav>

        {/* Footer */}
        {footer && (
          <div
            className={cn(
              "border-t py-2 shrink-0",
              isExpanded ? "px-3" : "px-2"
            )}
          >
            {footer}
          </div>
        )}

        {/* Expand/Collapse toggle */}
        <div
          className={cn(
            "border-t py-2 shrink-0",
            isExpanded ? "px-3" : "px-2"
          )}
        >
          <Tooltip delayDuration={0}>
            <TooltipTrigger asChild>
              <Button
                variant="ghost"
                size="sm"
                className={cn(
                  "w-full h-10",
                  isExpanded ? "justify-start gap-3" : "justify-center px-0"
                )}
                onClick={toggleExpanded}
              >
                {isExpanded ? (
                  <>
                    <ChevronLeft className="size-5 shrink-0" />
                    <span>Collapse</span>
                  </>
                ) : (
                  <ChevronRight className="size-5 shrink-0" />
                )}
              </Button>
            </TooltipTrigger>
            {!isExpanded && (
              <TooltipContent side="right" sideOffset={8}>
                Expand
              </TooltipContent>
            )}
          </Tooltip>
        </div>
      </aside>
    </TooltipProvider>
  );
}

export { SideNav, type SideNavProps, type SideNavItem };
