"use client"

import * as React from "react"
import { Switch as SwitchPrimitive } from "radix-ui"
import { Check, X } from "lucide-react"

import { cn } from "@/lib/utils"

interface SwitchProps
  extends React.ComponentProps<typeof SwitchPrimitive.Root> {
  checked?: boolean
}

function Switch({ className, checked, ...props }: SwitchProps) {
  return (
    <SwitchPrimitive.Root
      data-slot="switch"
      checked={checked}
      className={cn(
        "peer data-[state=checked]:bg-primary data-[state=unchecked]:bg-foreground/50 dark:data-[state=unchecked]:bg-foreground/30 focus-visible:border-ring focus-visible:ring-ring/50 inline-flex h-[1.25rem] w-9 shrink-0 items-center rounded-full border border-transparent shadow-xs transition-all outline-none focus-visible:ring-[3px] disabled:cursor-not-allowed disabled:opacity-50 px-0.75 relative data-[state=checked]:justify-start data-[state=unchecked]:justify-end [&_svg:not([class*='size-'])]:size-3 [&_svg:not([class*='text-'])]:text-primary-foreground",
        className
      )}
      {...props}
    >
      <SwitchPrimitive.Thumb
        data-slot="switch-thumb"
        className={cn(
          "bg-background dark:data-[state=unchecked]:bg-foreground dark:data-[state=checked]:bg-primary-foreground pointer-events-none block size-4 rounded-full ring-0 transition-transform absolute left-0 data-[state=checked]:translate-x-[calc(100%+1px)] data-[state=unchecked]:translate-x-0"
        )}
      />
      {checked ? <Check /> : <X />}
    </SwitchPrimitive.Root>
  )
}

export { Switch }
